// Validate webhook secret
const receivedSecret = $node['Webhook'].context['headers']['x-webhook-secret'];
const expectedSecret = $env.N8N_WEBHOOK_SECRET;

if (receivedSecret !== expectedSecret) {
  throw new Error('Unauthorized: Invalid webhook secret');
}

// Validate required fields
const payload = $input.item.json;
const required = ['submissionId', 'brand', 'productBlueprint', 'targetEnvironment'];

for (const field of required) {
  if (!payload[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Log submission
console.log('‚úÖ Valid submission received:', payload.submissionId);
console.log('üéØ Environment:', payload.targetEnvironment);
console.log('üè¢ Brand:', payload.brand.name);

return $input.item;

// Extract key information for analysis
const payload = $input.item.json;

const analysisContext = {
  submissionId: payload.submissionId,
  environment: payload.targetEnvironment,

  // Brand context
  brand: {
    name: payload.brand.name,
    voice: payload.brand.voice,
    values: payload.brand.values
  },

  // Product details
  product: {
    functions: payload.productBlueprint.functions,
    formType: payload.productBlueprint.formType,
    packaging: payload.productBlueprint.packagingType,
    netto: payload.productBlueprint.netto
  },

  // Ingredients
  ingredients: payload.ingredients.map(ing => ({
    name: ing.name,
    percentage: ing.percentage,
    purpose: ing.purpose
  })),

  // Target market
  market: {
    gender: payload.targetMarket.gender,
    ageRanges: payload.targetMarket.ageRanges,
    location: payload.targetMarket.geography
  },

  // Business context
  business: {
    distribution: payload.businessContext.distributionFocus,
    sustainability: payload.businessContext.sustainabilityPriority,
    regulatory: payload.businessContext.regulatoryPriority
  }
};

return { json: analysisContext };

