parameters.functionCode:
// Check regulatory requirements
const payload = $input.item.json;
const regulatory = payload.business.regulatory || [];

const checks = {
  submissionId: payload.submissionId,
  country: payload.market.location.country,
  requirements: [],
  status: 'pending'
};

// Indonesia specific checks
if (payload.market.location.country === 'ID') {
  if (regulatory.includes('BPOM')) {
    checks.requirements.push({
      authority: 'BPOM',
      type: 'Product Registration',
      status: 'required',
      estimatedTime: '6-12 months',
      documents: [
        'Product formulation',
        'Manufacturing facility license',
        'Safety assessment',
        'Stability test results'
      ]
    });
  }

  if (regulatory.includes('Halal')) {
    checks.requirements.push({
      authority: 'MUI/LPPOM',
      type: 'Halal Certification',
      status: 'required',
      estimatedTime: '3-6 months',
      documents: [
        'Ingredient list with sources',
        'Manufacturing process',
        'Facility audit'
      ]
    });
  }
}

checks.status = 'completed';
return { json: checks };

// Combine all analysis results
const items = $input.all();

const report = {
  submissionId: items[0].json.submissionId,
  generatedAt: new Date().toISOString(),
  environment: items[0].json.environment,

  sections: {
    brandAnalysis: items.find(i => i.json.brandAnalysis)?.json.brandAnalysis || 'N/A',
    productBlueprint: items.find(i => i.json.productBlueprint)?.json.productBlueprint || 'N/A',
    marketAnalysis: items.find(i => i.json.marketAnalysis)?.json.marketAnalysis || 'N/A',
    regulatoryCheck: items.find(i => i.json.requirements)?.json || {}
  },

  summary: {
    totalSections: 4,
    completedSections: 4,
    status: 'completed'
  }
};

return { json: report };

